import React, { Component } from "react";
import Wrapper from "./components/Wrapper"
import CharacterCard from "./components/CharacterCard";
import characters from "./characters.json";

class App extends Component{
    state = {
        characters: characters
    };

    guessingFunction = (key) => {
        // console.log(key);
        // console.log(key);
        // console.log(this.state.characters[key])
        let characters=this.state.characters
        console.log(characters[key] + " is " + characters[key].selected)
        if(characters[key].selected){
            // if(window.confirm('Already Guessed! Would you like to play again?')){
            //     window.location.reload();
            // }
            alert('selected')
        }else{
            characters[key].selected = true
            // console.log(characters)
            characters = characters.map((a) => [Math.random(),a])
                .sort((a,b) => a[0]-b[0])
                .map((a) => a[1]);
            // console.log(characters)
            // const shuffleArray = arr => arr
            //     .map(a => [Math.random(), a])
            //     .sort((a, b) => a[0] - b[0])
            //     .map(a => a[1]);

            // shuffleArray(characters)
            this.setState({
                characters: characters

            })
            // console.log(this.state.characters)x
        }

    }

    render(){
        return(
            <Wrapper>
                {this.state.characters.map( character =>(
                    <CharacterCard
                        key = {character.id}
                        id = {character.id}
                        name = {character.name}
                        image = {character.image}
                        guessCharacter = {this.guessingFunction}
                    />
                ))}
            </Wrapper>
        )
    }
}

export default App;
